import React from 'react';

const Todo = ({ todo, onTodoToggle }) => {
  const handleTodoToggle = () => {
    if (typeof onTodoToggle === 'function') {
      onTodoToggle(todo.id);
    }
  };

  return (
    <div className="todo">
      <label>
        <input type="checkbox" checked={todo.completed} onChange={handleTodoToggle} />
        {todo.task}
      </label>
    </div>
  );
};

export default Todo;
