import React, { useState } from 'react';
import TodoList from './TodoList';
import TodoForm from './TodoForm';

const App = () => {
  const [todos, setTodos] = useState([
    { id: 1, task: 'Learn React', completed: false },
    { id: 2, task: 'Build a React app', completed: false },
    { id: 3, task: 'Learn React Native', completed: false },
  ]);

  const addTodo = task => {
    const newTodo = { id: todos.length + 1, task, completed: false };
    setTodos([...todos, newTodo]);
  };

  const handleTodoToggle = id => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    });

    const completedTodos = updatedTodos.filter(todo => todo.completed === false);

    setTodos(completedTodos);
  };

  return (
    <div className="app">
      <h1>Todo List</h1>
      <TodoList todos={todos} handleTodoToggle={handleTodoToggle} />
      <TodoForm addTodo={addTodo} />
    </div>
  );
};

export default App;
