import React, { useState } from 'react';
import TodoList from './TodoList';
import TodoForm from './TodoForm';

const App = () => {
  const [todos, setTodos] = useState([
    { id: 1, task: 'Learn React', completed: false },
    { id: 2, task: 'Build a React app', completed: false },
    { id: 3, task: 'Learn React Native', completed: false },
  ]);

  const addTodo = task => {
    const newTodo = { id: todos.length + 1, task, completed: false };
    setTodos([...todos, newTodo]);
  };

  const handleTodoToggle = id => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    });

    setTodos(updatedTodos);
  };

  const handleTodoEdit = (id, newTask) => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        return { ...todo, task: newTask };
      }
      return todo;
    });

    setTodos(updatedTodos);
  };

  return (
    <div style={{alignItems: 'center', justifyContent: 'center', width: '100%', height: '100%'}}>
      <div className="app" style={{width: '20%', alignSelf: 'center', backgroundColor: '#fff'}}>
        <h1 style={{textAlign: 'center'}}>Todo List</h1>
        <TodoForm addTodo={addTodo} />
        <TodoList todos={todos} onTodoToggle={handleTodoToggle} onTodoEdit={handleTodoEdit} />
      </div>
    </div>

  );
};

export default App;
